sys_variable_value.value:
(function execute(inputs, outputs) {
    // 1. Capture inputs from the Flow
    var inputDate = inputs.date || gs.nowNoTZ();  
    var inputTable = inputs.table || 'task';  

    // 2. Query records
    var gr = new GlideRecord(inputTable);
    gr.addEncodedQuery('opened_at>=' + inputDate + '^state!=6');
    gr.setLimit(100);
    gr.query();

    // 3. Collect results
    var resultsArray = [];
    while (gr.next()) {
        var record = {
            number: gr.getValue('number'),
            short_description: gr.getValue('short_description'),
            state: gr.getValue('state'),
            priority: gr.getValue('priority'),
            opened_at: gr.getValue('opened_at'),
            closed_at: gr.getValue('closed_at'),
            assignment_group: gr.assignment_group ? gr.assignment_group.getDisplayValue() : '',
            assigned_to: gr.assigned_to ? gr.assigned_to.getDisplayValue() : '',
            reassignment_count: gr.getValue('reassignment_count'),
            work_notes: gr.getValue('work_notes'),
            description: gr.getValue('description'),
            category: gr.getValue('category'),
            active: gr.getValue('active'),
            sys_created_on: gr.getValue('sys_created_on'),
            sys_updated_on: gr.getValue('sys_updated_on'),
            sys_class_name: gr.getValue('sys_class_name')
        };
        resultsArray.push(record);
    }

    // 4. Convert array into a JSON string
    var jsonstring = JSON.stringify(resultsArray);

    // 5. Assign the string to the action's output variable
    outputs.results_json = jsonstring;

})(inputs, outputs);

